(function () {

  'use strict';

  var loadJS = function(url, implementationCode, location) {
    var scriptTag = document.createElement('script');
    scriptTag.src = url;

    scriptTag.onload = implementationCode;
    scriptTag.onreadystatechange = implementationCode;

    location.appendChild(scriptTag);
  };

  var TWEETER_JS_API_URL = 'https://platform.twitter.com/widgets.js';
  var TWITTER_EMBED_API = `${window.location.origin}/api/tweet.json?tweetId=`;
  tinymce.PluginManager.add("embed_tweet_static", function (editor, url) {
    var dialogConfig =  {
      title: 'Input Tweet ID',
      body: {
        type: 'panel',
        items: [
          {
            type: 'input',
            name: 'tweetId',
            label: 'Enter Tweet ID'
          }
        ]
      },
      buttons: [
        {
          type: 'cancel',
          name: 'closeButton',
          text: 'Cancel'
        },
        {
          type: 'submit',
          name: 'submitButton',
          text: 'Insert Code',
          primary: true
        }
      ],
      initialData: {
        tweetId: '',
      },
      onSubmit: function (api) {
        var data = api.getData();
        console.log(data);
        loadJS(TWEETER_JS_API_URL, function () {
          var doc = document.createElement('div');
          document.body.appendChild(doc);
          console.log(doc);
          console.log(window.twttr.widgets);
          const url = `${TWITTER_EMBED_API}/${data.tweetId}`;
          fetch(url)
          .then((res) => res.json())
          .then((data) => {
            console.log(data);
            tinymce.activeEditor.execCommand('mceInsertRawHTML', false, JSON.parse(data).html);
            api.close();
          });
        },
        document.body);
      }
    };

    function _showDialog() {
      editor.windowManager.open(dialogConfig);
    }

    // Define the Toolbar button
    editor.ui.registry.addButton('embed_tweet_static', {
        text: "Add Tweet Embed Static",
        onAction: _showDialog
    });
  });


  return {
    getMetadata: function () {
      return {
        name: "Embed Tweet Static",
        url: "http://exampleplugindocsurl.com"
      };
    }
  };
})();